<script>
export default {
  name: "ScrollTop02",
  data() {
    return {
      navActiveStatus:false,
    }
  },
  mounted() {
    window.addEventListener('wheel', this.wheeling);
  },
  created() {
    // 페이지 이동 시 스크롤 초기화.
    window.scrollTo(0, 0);
  },  
  unmounted() {
    ///////////////////////////////////
    window.removeEventListener('wheel', this.wheeling);
    ///////////////////////////////////
  },

  methods:{
    wheeling(e) {
      const delta = e.wheelDelta;
      (delta >= 50)
        ? this.navActiveStatus = false
        : this.navActiveStatus = true;
    }
  },
}
</script>

